<?php// Precisando de ajuda para desenvolver o seu sistema? acesse sistema-web-para.com.br e contrate uma assessoria...$file = 'https://viacep.com.br/ws/'.str_replace('-', '', $_GET['cep']).'/json/';if (function_exists('curl_version')){$curl = curl_init();curl_setopt($curl, CURLOPT_URL, $file);curl_setopt($curl, CURLOPT_RETURNTRANSFER, 1);$json = curl_exec($curl);curl_close($curl);}else if (file_get_contents(__FILE__) && ini_get('allow_url_fopen')){$json = file_get_contents($file);}else{echo 'O servidor nÃ£o tem cUrl instalado nem allow_url_fopen ativado!';}$obj = json_decode($json);?><script>document.getElementById('endereco').value = '<?php echo trim($obj->{'logradouro'}.' '.$obj->{'complemento'}); ?>';document.getElementById('bairro').value = '<?php echo $obj->{'bairro'} ?>';document.getElementById('cidade').value = '<?php echo $obj->{'localidade'} ?>';document.getElementById('estado').value = '<?php echo $obj->{'uf'}; ?>';</script>